    const tableflds = table.qFields;
    return tableflds.map(fld => ({
      table: table.qName,
      field: fld.qName
    }));
  })
    .reduce((acc, curr) => {
      return acc.concat(curr);
    }, []);

  document.querySelector("tbody").innerHTML = flds.map(fld => `${fld.table}${fld.field}`).join("");
});

